import React, { Component } from 'react';
var data = require('../controllers/Data.json');

class Header extends React.Component {
    render() {
        return (
            <header>
                <div className="container">
                    <nav>
                        <ul>
                            <li><a href={'http://twitter.com/' + data.social.twitter}><i className="fa fa-twitter"></i></a></li>
                            <li><a href={'http://github.com/' + data.social.github}><i className="fa fa-github"></i></a></li>
                            <li><a href={'http://linkedin.com/' + data.social.linkedin}><i className="fa fa-linkedin"></i></a></li>
                            <li><a href={'http://facebook.com/' + data.social.facebook}><i className="fa fa-facebook"></i></a></li>
                        </ul>
                    </nav>
                </div>
            </header>
        );
    }
}

module.exports = Header;

import React, { Component } from 'react';
import logo from '../images/Logo.png';
var data = require('../controllers/Data.json');

class Intro extends React.Component {
    render() {
        return (
            <div className="intro">
                <div className="intro-container container">
                    <h1>{data.intro} <span>{data.name}</span>.</h1>
                    <h2>{data.position}</h2>
                    <p>{data.body}</p>
                </div>
                <img src={logo} className="logo" />
            </div>
        );
    }
}

module.exports = Intro;

import React, { Component } from 'react';
var data = require('../controllers/Data.json');

class Experience extends React.Component {
    render() {
        return (
            <div className="jobs">
                <div className="container">
                    <h2>Experience.</h2>
                    <div className="job-container">
                        <div className="job-line"></div>
                        {data.experience.map((experience, key) =>
                            <div className="job" key={key}>
                                <div className="job-content">
                                    <img src={'/images/' + experience.logo} />
                                    <div className="position">{experience.position}</div>
                                    <a href={'http://' + experience.website}>{experience.company}</a>
                                    <div className="time">{experience.start} - {experience.end}</div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        );
    }
};

module.exports = Experience;

